<!DOCTYPE html>
<html lang="en">

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Post API Test Form</title>
</head>

<body>
    <h1>Post API Test Form</h1>

    <form id="postForm">
        <input type="hidden" id="postId" name="id">
        <label for="userId">User ID:</label>
        <input type="number" id="userId" name="user_id" required><br><br>

        <label for="categoryId">Category ID:</label>
        <input type="number" id="categoryId" name="category_id" required><br><br>

        <label for="title">Title:</label>
        <input type="text" id="title" name="title" required><br><br>

        <label for="description">Description:</label>
        <textarea id="description" name="description" required></textarea><br><br>

        <label for="content">Content (or Base64 string):</label>
        <textarea id="content" name="content" required></textarea><br><br>

        <button type="submit" id="submitBtn">Create Post</button>
    </form>

    <div id="response"></div>

    <script>
        const form = document.getElementById('postForm');
        const submitBtn = document.getElementById('submitBtn');
        const responseDiv = document.getElementById('response');

        // Your base64 string here
        const base64String = 'data:image/png;base64,';

        // Pre-fill the content textarea with the base64 string
        document.getElementById('content').value = base64String;

        form.addEventListener('submit', async (e) => {
            e.preventDefault();

            const formData = new FormData(form);
            const postId = document.getElementById('postId').value;

            let url = 'http://localhost/Byanjan/apis/post/add.php';
            if (postId) {
                url = 'http://localhost/Byanjan/apis/post/edit.php';
            }

            try {
                const response = await fetch(url, {
                    method: 'POST',
                    body: formData
                });

                const result = await response.json();
                responseDiv.innerHTML = `<pre>${JSON.stringify(result, null, 2)}</pre>`;

                if (result.status === 'success' && !postId) {
                    // If it's a successful create operation, update the form for edit
                    document.getElementById('postId').value = result.postId;
                    submitBtn.textContent = 'Update Post';
                }
            } catch (error) {
                responseDiv.innerHTML = `<pre>Error: ${error.message}</pre>`;
            }
        });
    </script>
</body>

</html>